import React from 'react';
import profile1 from './image/profile1.png';
import profile2 from './image/profile2.png';
import profile3 from './image/profile3.png';

const SingleComment = (props) => {
    return (
        <div className = "comment" style = {{backgroundColor: "red"}} >
                <a href = "/" className = "avatar">
                    <img src  = {props.picture} alt = "profile picture" style = {{height: "200%", width: "200%", clear: "both"}} />
                </a>
                <div className = "content">
                    <a href= "/" className = "author" style = {{clear: "both", marginLeft: "30px"}} >
                        {props.name}
                    </a>
                    <div className = "metadata" style = {{clear: "both", right: "500px"}}>
                        <span className = "date">
                            {props.time}
                        </span>
                    </div>
                    <div className = "text" style = {{clear: "both", marginLeft: "30px"}}>
                            {props.message}
                    </div>
                </div>
            </div>
    ) 
}

export default SingleComment;
